import { Component, OnInit, NgZone  } from '@angular/core';
import { Router } from '@angular/router';
import { CrudService } from 'src/app/service/crud.service';
import { FormGroup, FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent implements OnInit {

  ProductForm: FormGroup;

  constructor(
    public formBuilder: FormBuilder,
    private router: Router,
    private ngZone: NgZone,
    private crudService: CrudService
  ) {
    this.ProductForm = this.formBuilder.group({
      name: [''],
      barcode: [''],
      description: [''],
      manufacturer: [''],
      category: [''],
    })
  }

  ngOnInit(): void {

  }

  onSubmit():any{
    this.crudService.addProduct(this.ProductForm.value)
    .subscribe(()=>{
      console.log('Data added successfully')
      this.ngZone.run(()=> this.router.navigateByUrl('/products-list'))
    },(err)=>{
      console.log(err)
    })
  }

}
<div class="row justify-content-center mt-5">
  <div class="col-md-4">
    <form [formGroup]="ProductForm" (ngSubmit)="onSubmit()">
      <div class="mb-3">
        <label class="form-label">Nazwa:</label>
        <input type="text" formControlName="name" class="form-control" required>
      </div>
      <div class="mb-3">
        <label class="form-label">Kod kreskowy:</label>
        <input type="text" formControlName="barcode" class="form-control" required>
      </div>
      <div class="mb-3">
        <label class="form-label">Opis:</label>
        <textarea formControlName="description" class="form-control" rows="3"></textarea>
      </div>
      <div class="mb-3">
        <label class="form-label">Producent:</label>
        <input type="text" formControlName="manufacturer" class="form-control" required>
      </div>
      <div class="mb-3">
        <label class="form-label">Kategoria:</label>
        <input type="text" formControlName="category" class="form-control" required>
      </div>
      <div class="mb-3">
        <button type="submit" class="btn btn-primary">Dodaj</button>
      </div>
    </form>
  </div>
</div>
